      const pageSize = 5;
      const [currentPage, setCurrentPage] = useState(1);
      const totalPages = Math.ceil(data.length / pageSize);
      const startIndex = (currentPage - 1) * pageSize;
      const endIndex = startIndex + pageSize;
      const pageData = data.map((item, index) => ({ ...item, id: index })).slice(startIndex,endIndex);
      const handlePageChange = (event: React.ChangeEvent<unknown>, page: number) => {
        setCurrentPage(page);
      };

    return(
        <>
            <TableContainer component={Paper} style={{ maxWidth: '96%', marginLeft:'30px' }}>
      <Table sx={{ minWidth: 650 }} aria-label="caption table">
        <caption></caption>
        <TableHead>
          <TableRow>
            <TableCell align="center" sx={{ minWidth: '90px' }}>신청일</TableCell>
            <TableCell align="center" sx={{ minWidth: '110px' }}>광고상품</TableCell>
            <TableCell align="center" sx={{ minWidth: '70px' }}>작업구분</TableCell>
            <TableCell align="center">작업명</TableCell>
            <TableCell align="center">작업상태</TableCell>
            <TableCell align="center">수행시작</TableCell>
            <TableCell align="center">수행종료</TableCell>
            <TableCell align="center">등록파일</TableCell>
            <TableCell align="center">결과</TableCell>
            <TableCell align="center">대기취소</TableCell>
            <TableCell align="center">요청자</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {pageData.map((row) => (
            <TableRow key={row.id}>
              <TableCell component="th" scope="row" sx={{ fontSize: '13.5px' }}>
              {(() => {
                    const date = row.createdAt.toLocaleString().split(' ')[0];
                    const time = row.createdAt.toLocaleString().split(' ')[1];
                    return (
                        <div>
                            {date}<br />{time}
                        </div>
                    );
                })()}
              </TableCell>
              <TableCell align="center" sx={{ fontSize: '13.5px' }}>{row.mediaName}</TableCell>
              <TableCell align="center" sx={{ fontSize: '13.5px' }}>{row.type}</TableCell>
              <TableCell align="center" sx={{ fontSize: '13.5px' }}>{row.name}</TableCell>
              <TableCell align="center" sx={{ fontSize: '13.5px' }}>
              {
                (() => {
                const statusText = row.status?.toString() || '';
                const statusColor = KeywordJobStatusMap[statusText]?.color || '';
                const text = KeywordJobStatusMap[statusText]?.text || '';
                return (
                    <div
                    style={{
                        color: statusColor,
                        width: 'fit-content',
                        fontWeight: 'bolder',
                    }}
                    dangerouslySetInnerHTML={{ __html: text }}
                    />
                );
                })()
            }
              </TableCell>
              <TableCell align="center" sx={{ fontSize: '13.5px' }}>
                {/* startAt 변환 및 출력 */}
                <div dangerouslySetInnerHTML={{ __html: row.startAt }} />
              </TableCell>
              <TableCell align="center" sx={{ fontSize: '13.5px' }}>
                {/* endAt 변환 및 출력 */}
                <div dangerouslySetInnerHTML={{ __html: row.endAt }} />
              </TableCell>
              <TableCell align="center" sx={{ fontSize: '13.5px' }}>
                <Button variant="outlined" color="error">
                  다운로드
                </Button>
              </TableCell>
              <TableCell align="center" sx={{ fontSize: '13.5px' }}>
                {row.status === 'S' || row.status === 'P' ? (
                  <Button variant="outlined" color="error" >
                    다운로드
                  </Button>
                ) : (
                  <Button variant="contained" disabled >
                    다운로드
                  </Button>
                )}
              </TableCell>
              <TableCell align="center" sx={{ fontSize: '13.5px' }}>
                <Button variant="contained" disabled>
                  취소
                </Button>
              </TableCell>
              <TableCell align="center" sx={{ fontSize: '13.5px' }}>{row.createdBy}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
            <Pagination
                    count={totalPages}
                    page={currentPage}
                    onChange={handlePageChange}
                    color="primary"
            /> 
             </TableContainer>   
      </>
    );
}
