import { Button } from '@mui/material';
import {siteNo} from '../api/keywordJob'
import { useEffect, useState } from 'react';
import '../CSS/keywordCheckTable.css'
import { DataGrid,GridCellParams,GridColDef } from '@mui/x-data-grid';
import KeywordCheck from './KeywordCheck';
import { KeywordJobStatusMap } from '../constants/KeywordRegistStatusList';

export interface DataItem{
    createdAt: Date,
    mediaName: string,
    type: string,
    name: string,
    status: string,
    startAt:Date,
    endAt:Date,
    fileName:string,
    createdBy:string,
    [key: string]: any;
}

interface KeywordCheckTableProps {
    data: DataItem[];
  }
export default function KeywordCheckTable({ data }: KeywordCheckTableProps){

      const columns: GridColDef[] = [
        { 
            field: 'createdAt', headerName: '신청일', width: 100,headerAlign: 'center',
            valueGetter: (params) => {
                const { createdAt } = params.row;
                const date = createdAt.split(' ')[0];
                const time = createdAt.split(' ')[1];
                return `${date}<br />${time}`;
              },
              renderCell: (params) => (
                <div dangerouslySetInnerHTML={{ __html: params.value }} />
              ),
            },
        { field: 'mediaName', headerName: '광고상품', width: 150 },
        { field: 'type', headerName: '작업구분', width: 100},
        { field: 'name', headerName: '작업명', width: 150 },
        { field: 'status', headerName: '작업상태', width: 100,renderCell: (params: GridCellParams) => {
            const statusText = params.value?.toString() || '';
            const statusColor = KeywordJobStatusMap[statusText]?.color || '';
            const text = KeywordJobStatusMap[statusText]?.text || '';
            return (
              <div
                style={{
                  color: statusColor,
                  width: 'fit-content',
                  fontWeight : 'bolder',
                }}
              >
                {text}
              </div>
            );
          },},
        {
            field: 'startAt', headerName: '수행시작', width: 110 ,           
            valueGetter: (params) => {
                const { createdAt } = params.row;
                const date = createdAt.split(' ')[0];
                const time = createdAt.split(' ')[1];
                return `${date}<br />${time}`;
            },
            renderCell: (params) => (
                <div dangerouslySetInnerHTML={{ __html: params.value }} />
            ),
        },
        { 
            field: 'endAt', headerName: '수행종료', width: 110,
            valueGetter: (params) => {
                const { createdAt } = params.row;
                const date = createdAt.split(' ')[0];
                const time = createdAt.split(' ')[1];
                return `${date}<br />${time}`;
            },
            renderCell: (params) => (
                <div dangerouslySetInnerHTML={{ __html: params.value }} />
            ),},
        {
            field: 'registfile',
            headerName: '등록파일',
            width: 100,
            renderCell: (params: GridCellParams) => (
              <Button variant="outlined" color="error">
                다운로드
              </Button>
            ),
            headerAlign: 'center'
          },
          {
            field: 'fileresult',
            headerName: '결과',
            width: 100,
            renderCell: (params: GridCellParams) => (
              <Button variant="outlined" color="error">
                다운로드
              </Button>
            ),
            headerAlign: 'center'
          },
          {
            field: 'waitcancel',
            headerName: '대기취소',
            width: 100,
            renderCell: (params: GridCellParams) => (
              <Button variant="contained" disabled>
                취소
              </Button>
            ),
          },
        { field: 'createdBy', headerName: '요청자', width: 220,headerAlign: 'center'},
      ];
    return(
        <>
            <div style={{ height: 600, width: '93%',marginLeft:'45px'}}>
                    <DataGrid
                        rows={data.map((item, index) => ({ ...item, id: index }))}
                        columns={columns}
                        initialState={{
                            pagination: {
                            paginationModel: { page: 0, pageSize: 10 },
                            },
                        }}
                        autoHeight
                        sx={{
                            m:1,
                            boxShadow: 2,
                            border: 1,
                            borderColor: 'primary.light',
                            '& .MuiDataGrid-cell:hover': {
                            color: 'primary.main',
                            },
                            fontSize: 13,
                        }}
                        disableColumnMenu
                        />
        </div>
      </>
    );
}
