    return(
        <>
            <div style={styles1}>
                <div style={{width:'230px',height:'50px',marginTop:'20px',marginLeft:'700px'}}>
                <div style={{marginTop:'25px',fontWeight:'bold',fontSize:'13px',float:'left'}}>검색: </div>
                <TextField
                 id="outlined-basic" value={searchText}
                 onChange={handleSearchTextChange}
                 label={<Typography 
                 style={{fontSize:'13px',marginTop:'2px'}}>
                    검색어를입력하세유
                 </Typography>} variant="outlined" size="small" style={{marginTop:'10px',marginRight:'1px',float:'right'}}/>
                </div>
                <div style={{width:'360px',height:'50px',marginTop:'20px'}}>
                <div style={{marginLeft:'30px',marginTop:'25px',fontWeight:'bold',fontSize:'13px',float:'left'}}>작업:</div>
                {KeywordRegistStatusList.map((item)=>(
                    <FormControlLabel key={item.value}value="female" style={{marginTop:'13px'}} control={<Radio size="small" style= {{marginLeft:'5px'}}/>} label={<Typography style={{fontSize:'13px',marginLeft:'-6px'}}>{item.text}</Typography>}/>
                ))}
                </div>
                <Button variant="outlined" style={{width:'70px',height:'38px',marginTop:'30px'}} color="error">비우기</Button>
                <Button variant="outlined" style={{width:'90px',height:'38px',marginLeft:'10px',marginTop:'30px'}}onClick={handleSearch}>검색하기</Button>
            </div>
            <KeywordCheckTable searchText={searchText} onSearch={handleSearch}/>
        </>
    );
}
