import { Button, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@mui/material';
import {makeStyles} from '@mui/material';
import {siteNo} from '../api/keywordJob'
import { useEffect, useState } from 'react';
import KeywordRegistStatusList, { KeywordJobStatusMap } from '../constants/KeywordRegistStatusList';
import '../CSS/keywordCheckTable.css'
import { DataGrid,GridCellParams,GridColDef } from '@mui/x-data-grid';
import { useDemoData } from '@mui/x-data-grid-generator';

export default function KeywordCheckTable(){
    interface DataItem{
        createdAt: string,
        mediaName: string,
        type: string,
        name: string,
        status: string,
        startAt:string,
        endAt:string,
        fileName:string,
        createdBy:string,
    }
    const columns: GridColDef[] = [
        { field: 'createdAt', headerName: '신청일', width: 150 },
        { field: 'mediaName', headerName: '광고상품', width: 150 },
        { field: 'type', headerName: '작업구분', width: 150 },
        { field: 'name', headerName: '작업명', width: 150 },
        { field: 'status', headerName: '작업상태', width: 150 },
        { field: 'startAt', headerName: '수행시작', width: 150 },
        { field: 'endAt', headerName: '수행종료', width: 150 },
        {
            field: 'registfile',
            headerName: '등록파일',
            width: 150,
            renderCell: (params: GridCellParams) => (
              <Button variant="outlined" color="error">
                다운로드
              </Button>
            ),
          },
          {
            field: 'fileresult',
            headerName: '결과',
            width: 150,
            renderCell: (params: GridCellParams) => (
              <Button variant="outlined" color="error">
                다운로드
              </Button>
            ),
          },
          {
            field: 'waitcancel',
            headerName: '대기취소',
            width: 150,
            renderCell: (params: GridCellParams) => (
              <Button variant="contained" disabled>
                취소
              </Button>
            ),
          },
        { field: 'createdBy', headerName: '요청자', width: 150 },
      ];

    const [data, setData] = useState<DataItem[]>([]);
    useEffect(() => {
        const fetchData = async () => {
          try {
            const response = await siteNo()
            const users: any = response.data;
            console.log(users);
            const contents: any[] = users.content;
            console.log(contents);
            const getStatusText = (status: string): string => {
                const matchedStatus = KeywordJobStatusMap[status];
                return matchedStatus ? matchedStatus.text : "";
              };
            const processedData = contents.map((item: any) => {
                const statusText = getStatusText(item.status);
                const formattedStatus = item.createdAt ? item.createdAt.replace('T', ' ') : '';
                const startText = item.startAt ? item.startAt.replace('T',' ') : '';
                const endText = item.endAt ? item.endAt.replace('T','') : '';
                const matchedtype = KeywordRegistStatusList.find(
                  (status) => status.value === item.type
                );
                const typeText = matchedtype ? matchedtype.text : ""; 
                return {
                  createdAt: formattedStatus,
                  mediaName: item.mediaName,
                  type: typeText,
                  name: item.name,
                  status: statusText,
                  startAt: startText,
                  endAt: endText,
                  fileName: item.keywordJobNo,
                  createdBy: item.createdBy,
                };
              });

              setData(processedData);
          } catch (error) {
            console.error('삐빅삐빅 에러입니다:', error);
          }
        };
        fetchData();
      }, []);

    return(
        <div style={{ height: 600, width: '95%',marginLeft:'40px'}}>
                <DataGrid
                    rows={data.map((item, index) => ({ ...item, id: index }))}
                    columns={columns}
                    initialState={{
                        pagination: {
                          paginationModel: { page: 0, pageSize: 10 },
                        },
                      }}
                      pageSizeOptions={[5, 10]}
                    />
      </div>
    );
}
